import React from 'react'
import { Button } from 'semantic-ui-react'


class Question extends React.Component {

    state = {
        answer: null
    }

    handleClick = (e) => {
        this.props.answerQuestion(e, this.props.question.id, this.props.question.answer)
        this.setState({
            answer: e.target.value
        })
    }

    render() {
        return (
            <div className="question-container">
                <div className="question">
                    <h3>{this.props.question.text}</h3>
                    <Button.Group size='large' onClick={this.handleClick}>
                        <Button positive value="true" content="True" />
                        <Button negative value="false" content="False" />
                    </Button.Group>
                    <div className="answer">
                        {this.state.answer && `You answered ${this.state.answer}. The correct answer is ${this.props.question.answer}`}
                    </div>
                </div>

            </div>
        )
    }

}

export default Question
